void TitleMeshField::Init()
{
	
	

	//í∏ì_ÉoÉbÉtÉ@ê∂ê¨
	{
		for (int x = 0; x < MESH_X; x++)//
		{
			for (int z = 0; z < MESH_Z; z++)//
			{
				m_Vertex[x][z].Position = XMFLOAT3((x - 50) * 2.0f, g_MeshFieldHeight[x][z], (z - 50) * -2.0f);
				m_Vertex[x][z].Normal = XMFLOAT3(0.0f, 1.0f, 0.0f);
				m_Vertex[x][z].Diffuse = XMFLOAT4(1.0f, 1.0f, 1.0f, 1.0f);
				m_Vertex[x][z].TexCoord = XMFLOAT2(x, z);
			}
		}

		//ñ@ê¸ÉxÉNÉgÉãåvéZ
		for (int x = 1; x < (MESH_X - 1); x++)//
		{
			for (int z = 1; z < (MESH_Z - 1); z++)//
			{
				XMFLOAT3 vx, vz, vn;
				vx.x = m_Vertex[x + 1][z].Position.x - m_Vertex[x - 1][z].Position.x;
				vx.y = m_Vertex[x + 1][z].Position.y - m_Vertex[x - 1][z].Position.y;
				vx.z = m_Vertex[x + 1][z].Position.z - m_Vertex[x - 1][z].Position.z;

				vz.x = m_Vertex[x][z - 1].Position.x - m_Vertex[x][z + 1].Position.x;
				vz.y = m_Vertex[x][z - 1].Position.y - m_Vertex[x][z + 1].Position.y;
				vz.z = m_Vertex[x][z - 1].Position.z - m_Vertex[x][z + 1].Position.z;

				//äOêœ
				vn.x = vz.y * vx.z - vz.z * vx.y;
				vn.y = vz.z * vx.x - vz.x * vx.z;
				vn.z = vz.x * vx.y - vz.y * vx.x;

				//ê≥ãKâª(í∑Ç≥ÇPÇ…Ç∑ÇÈ)
				float len = sqrtf(vn.x * vn.x + vn.y * vn.y + vn.z * vn.z);
				vn.x /= len;
				vn.y /= len;
				vn.z /= len;

				m_Vertex[x][z].Normal = vn;

			}

		}

		D3D11_BUFFER_DESC bd;
		ZeroMemory(&bd, sizeof(bd));
		bd.Usage = D3D11_USAGE_DEFAULT;
		bd.ByteWidth = sizeof(VERTEX_3D) * (MESH_X) *(MESH_Z);//
		bd.BindFlags = D3D11_BIND_VERTEX_BUFFER;
		bd.CPUAccessFlags = 0;

		D3D11_SUBRESOURCE_DATA sd;
		ZeroMemory(&sd, sizeof(sd));
		sd.pSysMem = m_Vertex;

		Renderer::GetDevice()->CreateBuffer(&bd, &sd, &m_VertexBuffer);
	}

	//ÉCÉìÉfÉbÉNÉXÉoÉbÉtÉ@
	{
		unsigned int index[((MESH_X + 1) * 2) * (MESH_Z - 1) - 2];//

		int i = 0;
		for (int x = 0; x < MESH_X - 1; x++)//
		{
			for (int z = 0; z < MESH_Z; z++)//
			{
				index[i] = x * MESH_X + z;//
				i++;

				index[i] = (x + 1) * (MESH_X) + z;//
				i++;
			}
			if (x == (MESH_X - 2))//
				break;
			index[i] = (x + 1) * (MESH_X) + (MESH_Z - 1);//
			i++;

			index[i] = (x + 1) * (MESH_X);//
			i++;
		}
		D3D11_BUFFER_DESC bd;
		ZeroMemory(&bd, sizeof(bd));
		bd.Usage = D3D11_USAGE_DEFAULT;
		bd.ByteWidth = sizeof(unsigned int) * ((MESH_X + 1) * 2) * (MESH_X - 1) - 2;//
		bd.BindFlags = D3D11_BIND_INDEX_BUFFER;
		bd.CPUAccessFlags = 0;

		D3D11_SUBRESOURCE_DATA sd;
		ZeroMemory(&sd, sizeof(sd));
		sd.pSysMem = index;

		Renderer::GetDevice()->CreateBuffer(&bd, &sd, &m_IndexBuffer);
	}

	//ÉeÉNÉXÉ`ÉÉì«Ç›çûÇ›
	m_Texture = TexturePool::GetInstance()->GetTexture("Ground");

	Renderer::CreateVertexShader(&m_VertexShader, &m_VertexLayout,
		"shader\\vertexLightingVS.cso");

	Renderer::CreatePixelShader(&m_PixelShader,
		"shader\\vertexLightingPS.cso");

}

void TitleMeshField::Uninit()
{
	m_VertexBuffer->Release();
	m_IndexBuffer->Release();

	m_VertexLayout->Release();
	m_VertexShader->Release();
	m_PixelShader->Release();
}

