void Wave::Draw()
{


	//ì¸óÕÉåÉCÉAÉEÉgê›íË
	Renderer::GetDeviceContext()->IASetInputLayout(m_VertexLayout);

	//ÉVÉFÅ[É_Å[ê›íË
	Renderer::GetDeviceContext()->VSSetShader(m_VertexShader, NULL, 0);
	Renderer::GetDeviceContext()->PSSetShader(m_PixelShader, NULL, 0);

	//ÉèÅ[ÉãÉhÉ}ÉgÉäÉNÉXê›íË
	XMMATRIX world, scale, rot, trans;
	scale = XMMatrixScaling(m_Size.x, m_Size.y, m_Size.z);
	rot = XMMatrixRotationRollPitchYaw(m_Rotation.x, m_Rotation.y, m_Rotation.z);
	trans = XMMatrixTranslation(m_Position.x, m_Position.y, m_Position.z);
	world = scale * rot * trans;
	Renderer::SetWorldMatrix(world);

	//í∏ì_ÉoÉbÉtÉ@ê›íË
	UINT stride = sizeof(VERTEX_3D);
	UINT offset = 0;
	Renderer::GetDeviceContext()->IASetVertexBuffers(0, 1, &m_VertexBuffer, &stride, &offset);

	//ÉCÉìÉfÉbÉNÉXÉoÉbÉtÉ@ê›íË
	Renderer::GetDeviceContext()->IASetIndexBuffer(m_IndexBuffer, DXGI_FORMAT_R32_UINT, 0);

	//É}ÉeÉäÉAÉãê›íË
	MATERIAL material;
	ZeroMemory(&material, sizeof(material));
	material.Diffuse = XMFLOAT4(1.0f, 1.0f, 1.0f, 0.7f);
	material.TextureEnable = true;
	Renderer::SetMaterial(material);

	//ÉeÉNÉXÉ`ÉÉê›íË
	Renderer::GetDeviceContext()->PSSetShaderResources(0, 1, &m_Texture);

	//ÉvÉäÉ~ÉeÉBÉuÉgÉ|ÉçÉWê›íË
	Renderer::GetDeviceContext()->IASetPrimitiveTopology(D3D11_PRIMITIVE_TOPOLOGY_TRIANGLESTRIP);

	//É|ÉäÉSÉìê›íË
	//Renderer::GetDeviceContext()->Draw(21 * 21, 0);
	Renderer::GetDeviceContext()->DrawIndexed(((WAVE_X + 1) * 2) * (WAVE_Z - 1) - 2, 0, 0);

}

float Wave::GetHeight(XMFLOAT3 pos)
{
	int x, z;
	//ÉuÉçÉbÉNî‘çÜéZèo
	x = pos.x / 5.0f + 10.0f;		//InitÇ≈ê›íËÇµÇƒÇÈíl
	z = pos.z / -5.0f + 10.0f;

	XMFLOAT3 pos0, pos1, pos2, pos3;
	pos0 = m_Vertex[x][z].Position;
	pos1 = m_Vertex[x + 1][z].Position;
	pos2 = m_Vertex[x][z + 1].Position;
	pos3 = m_Vertex[x + 1][z + 1].Position;

	XMFLOAT3 v12, v1p;
	v12.x = pos2.x - pos1.x;
	v12.y = pos2.y - pos1.y;
	v12.z = pos2.z - pos1.z;

	v1p.x = pos.x - pos1.x;
	v1p.y = pos.y - pos1.y;
	v1p.z = pos.z - pos1.z;

	//äOêœ
	float cy = v12.z * v1p.x - v12.x * v1p.z;

	float py;
	XMFLOAT3 n;
	if (cy > 0.0f)
	{
		//ç∂è„É|ÉäÉSÉì
		XMFLOAT3 v10;
		v10.x = pos0.x - pos1.x;
		v10.y = pos0.y - pos1.y;
		v10.z = pos0.z - pos1.z;

		//äOêœ
		n.x = v10.y * v12.z - v10.z * v12.y;
		n.y = v10.z * v12.x - v10.x * v12.z;
		n.z = v10.x * v12.y - v10.y * v12.x;
	}
	else
	{
		//âEè„É|ÉäÉSÉì
		XMFLOAT3 v13;
		v13.x = pos3.x - pos1.x;
		v13.y = pos3.y - pos1.y;
		v13.z = pos3.z - pos1.z;

		//äOêœ
		n.x = v12.y * v13.z - v12.z * v13.y;
		n.y = v12.z * v13.x - v12.x * v13.z;
		n.z = v12.x * v13.y - v12.y * v13.x;
	}

	//çÇÇ≥éÊìæ
	py = -((pos.x - pos1.x) * n.x + (pos.z - pos1.z) * n.z) / n.y + pos1.y;

	return py;
}
